import rospy
from prosp import ProspEnv
import numpy as np

env = ProspEnv()
print(env.reset())

r = rospy.Rate(10)
cnt = 0
while not rospy.core.is_shutdown():
    cnt += 1
    r.sleep()
    s, reward, d, _ = env.step(np.array([1.23]))
    print(s)
    print(reward)
    print(d)
    print(cnt)
    if d:
        env.reset()

print('Finished!')
