import gym
from gym import spaces
import numpy as np
from math import *

class TwoObEnv(gym.Env):



    def __init__(self):
        self.action_space = spaces.Box(low=np.array([0.0]), high=np.array([1.0]))
        self.observation_space = spaces.Box(low=np.array([0.0]), high=np.array([1.0]))

        print('------------------- initialize two_ob -------------------')



    def step(self, action):
        assert self.action_space.contains(action), "%r (%s) invalid"%(action, type(action))
        observation = fabs(action[0] - 0.5) * 2
        observation = 1.0 - observation
        observation = np.array([observation])
        reward = observation[0]
        return observation, reward, False, {}



    def reset(self):
        return np.array([0.0])
